import React, { Component } from 'react';
import Order from '../../components/Order/Order';
import axios from '../../axios-order';
import { connect } from 'react-redux'

class Orders extends Component {
	state ={
		orders: []
	}

	componentDidMount() {
		axios.get('https://burger-labz.firebaseio.com/orders.json').then( response => {
			let orderArray = [];
			for(let order in response.data) {
				orderArray.push({
					...response.data[order],
					id: order
				})
			}
      this.setState({ orders: orderArray});
    });
	}

	render(){
		let ordersCollection = null;
		ordersCollection = this.state.orders.map((order)=>{
			return <Order customer={order.customer} 
										deliveryMethod={order.deliveryMethod} 
										ingredients={order.ingredients}
										price={order.price}
										key={'order' + order.id}/>
		})

		return(
			<div>
				{ ordersCollection }
			</div>
		);
	}
}

export default Orders;
