import * as actionTypes from '../actions/actionTypes';
import axios from 'axios';


export const formSubmitHandler = (email, password, isSignUp) => {
	return dispatch => {
		dispatch(authStart())
		const authData = {
			email: email,
			password: password,
			returnSecureToken: true
		}
		url = 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyAyoU9vWd9oZJiKWFZ6qFBI5hKzfGHTy-4'
		if(isSignUp){
			url = ''
		}
		axios.post(url, authData)
		.then(response => {
			console.log(response)
		}).catch(error => {
			console.log(error)
		})
	}
} 

const authStart = () => {
	return {
		type: actionTypes.AUTH_START
	}
}

const authSuccess = () => {
	return {
		type: actionTypes.AUTH_SUCCESS
	}
}


const authEnd = () => {
	return {
		type: actionTypes.AUTH_END
	}
}
