import React from 'react';
import classes from './Modal.css'
const modal = (props) => {
	console.log(props.show)
	return (
		<div className={classes.Modal}
				 style={{
				 		transform: props.show ? 'transalateY(0)' : 'transalateY(-100)vh',
				 		opacity: props.show ? '1' : '0'
				 }}>
			{props.children}
		</div>
	)
}

export default modal;
