import * as actionTypes from '../actions'

const intialState = {
	ingredients: {},
	totalPrice: 4
}

const reducer = (state = intialState, action) => {
	switch(action.type) {
		case (actionTypes.SET_INGREDIENTS):
			return {
				ingredients: action.ingredients
			}
		case (actionTypes.INC_INGREDIENTS):
			console.log(action.ingredientName)
			let ingredientCount = state.ingredients[action.ingredientName]
			let ingredients = {...state.ingredients};
			ingredients[action.ingredientName] = ingredientCount + 1;
			// let burgerPrice = INGREDIENTS_PRICES[ingredientName] + this.state.totalPrice;
			// this.setState({ ingredients: ingredients, totalPrice: burgerPrice, purchaseable: true });
			return {
				ingredients: ingredients
			}
			return {
				state
			}
		case (actionTypes.DEC_INGREDIENTS):
			return {
				state
			}
		default:
			return state
	}
}

export default reducer;
