import React, { Component } from 'react';
import CheckoutSummary from '../../components/Order/CheckoutSummary';

class Checkout extends Component {
	state = {
		ingredients: {
			salad: 1,
			bacon: 1,
			meat: 1,
			cheese: 1
		}
	}
	componentDidMount () {
		const search = '?bacon=1&cheese=1&meat=1&salad=1'
		const searchParams = new URLSearchParams(search);
		for(var pair of searchParams.entries()) {
		   console.log(pair[0]+ ', '+ pair[1]); 
		}
		// const query = new URLSearchParams(this.props.location.search);
		// console.log(this.props.location.search);
		// console.log(query.entries());
		// for (let param in params.entries()){
		// 	console.log(param[0]);
		// }
	}

	cancelCheckoutHandler = () => {
		this.props.history.goBack();
	}

	continueCheckoutHandler = () => {
		this.props.history.replace('/checkout/contact-order');
	} 

	render (){
		return (
			<CheckoutSummary 
				ingredients={this.state.ingredients} 
				cancelCheckoutHandler={this.cancelCheckoutHandler}
				continueCheckoutHandler={this.continueCheckoutHandler}/>
		)
	}
}

export default Checkout;
