import * as actionTypes from '../actions/actionTypes';
import axios from '../../axios-order';

export const checkoutOrder = (data) => {
	return dispatch => {
		dispatch(trySubmitOrder())
		axios.post('/orders.json', data).then(response => {
			dispatch()
			this.props.history.push('/');
		}).catch(error => {
			// dispatch(trySubmitOrder(false))
		})
	}
}

export const submitOrderSuccess = (order, id) => {
	return {
		type: actionTypes.ORDER_SUBMIT_SUCCESS,
		order: order,
		id: id
	}
}

export const trySubmitOrder = () => {
	return {
		type: actionTypes.TRY_SUBMIT
	}
}
