import React from 'react';
import classes from './Input.css';

const input = (props) => {
	console.log(props.options);
	// let burgerIngredients = Object.keys(props.ingredients).map( igKey => {
	// 	return [...Array(props.ingredients[igKey])].map( (_, i) => {
	// 		return <BurgerIngredient key={igKey+i} type={igKey}/>
	// 	});
	// }).reduce((arr, el) => {
	// 	return arr.concat(el)
	// }, []);

	let inputElement = null;
	switch(props.inputtype) {
		case ('input'):
			inputElement = <input className={classes.InputElement} {...props}/>
			break;
		case ('textarea'):
			inputElement = <textarea className={classes.InputElement} {...props}/>
			break;
		case ('select'):
			inputElement = <select className={classes.InputElement} {...props}>
											{
												Object.keys(props.options).map(option => {
													return <option> {props.options[option]}</option>
												})
											}
			 							 </select>
			break;
		default:
			inputElement = <input className={classes.InputElement} {...props}/>
	}

	return (
		<div className={classes.Input}>
			<label className={classes.Input}> {props.label} </label>
			{inputElement}
		</div>
	);
}


export default input;
