import * as actionTypes from '../actions'

const intialState = {
	ingredients: {}
}

const reducer = (state = intialState, action) => {
	console.log(action)

	switch(action.type) {
		case (actionTypes.SET_INGREDIENTS):
			const obj = {
				ingredients: {}
			}
			console.log(obj)
			return obj
		case (actionTypes.INC_INGREDIENTS):
			return {
				state
			}
		case (actionTypes.DEC_INGREDIENTS):
			return {
				state
			}
		default:
			return{
				state
			}
	}
}

export default reducer;
