import React, { Component } from 'react';
import Input from '../../components/UI/Input/FormInput';
import Button from '../../components/UI/Button/Button';
import classes from './Auth.css';

class Auth extends Component {
	state = {
		controls: {
			email: {
				elementType: 'input',
				elementConfig: {
					type: 'email',
					placeholder: 'Mail Address'
				},
				value: '',
				validation: {
					required: true, 
					isEmail: true
				},
				valid: false,
				touched: false
			},
			password: {
				elementType: 'input',
				elementConfig: {
					type: 'password',
					placeholder: 'Password'
				},
				value: '',
				validation: {
					required: true, 
					minLength: 6
				},
				valid: false,
				touched: false
			}
		}
	}

	inputChangeHandler = (field, event) => {
		console.log('here')
		console.log(field)
		console.log(this.state.controls['email'])

		const updatedControls = {
			...this.state.controls,
			[event]: {
				...this.state.controls,
				value: event.target.value,
				valid: this.checkValidity(event.target.value, this.state.controls[event].validation),
				touched: true
			}
		}

		this.setState({controls: updatedControls})
	}

	checkValidity(value, rules) {
		let isValid = true;

		if(!rules) {
			return true;
		}

		if(rules.required){
			isValid = value.trim() !== '' && isValid
		}

		if(rules.minLength) {
			isValid = value.lenth >= rules.minLength && isValid
		}

		if(rules.maxLength) {
			isValid = value.lenth <= rules.maxLength && isValid
		}

		if(rules.isValid) {
			const pattern = /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/
			isValid = pattern.test(value) && isValid
		}

		if(rules.isNumeric) {
			const pattern = /^\d+$/;
			isValid = pattern.test(value) && isValid
		}

		return isValid;
	}

	render() {
		let formElements = [];
		for (let key in this.state.controls) {
			formElements.push({
				id: key,
				config: this.state.controls[key]
			})
		}

		let form = (
			<form> 
				{
					formElements.map(field => (
						<Input 
							key={field.id}
							inputtype={field.config.elementType}
							elementConfig={field.config.elementConfig}
							value={field.config.value}
							invalid={!field.config.valid}
							shouldValidate={field.config.validation}
							touched={field.config.touched} 
							change={this.inputChangeHandler.bind(this, field.id)}/>
					))
				}
			</form>
		)
		return (
			<div className={classes.Auth}>
				{form}
				<Button btnType="Success" text="Submit"/>
			</div>
		);
	}
}

export default Auth;
