import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import CheckoutSummary from '../../components/Order/CheckoutSummary';
import ContactData from './ContactData/ContactData';
import Spinner from '../../components/UI/Spinner/Spinner';

class Checkout extends Component {
	state = {
		ingredients: {},
		totalPrice: 0
	}
	componentDidMount () {
		const query = new URLSearchParams(this.props.location.search);
		const ingredients = {}
		let totalPrice = null;
		for(let param of query.entries()) {
			if(param[0] == 'totalPrice'){
				totalPrice = param[1]
			}
			else{
				ingredients[param[0]] = +param[1]
			}
		}
		this.setState({ingredients: ingredients, totalPrice: totalPrice})
	}

	cancelCheckoutHandler = () => {
		this.props.history.goBack();
	}

	continueCheckoutHandler = () => {
		this.props.history.replace('/burger-checkout/contact-order');
	} 

	render (){
		let spinner = null;
		if(this)
		return (
			<div>
				<Spinner />
				<CheckoutSummary 
					ingredients={this.state.ingredients} 
					cancelCheckoutHandler={this.cancelCheckoutHandler}
					continueCheckoutHandler={this.continueCheckoutHandler}/>
	      <Route 
	      	path={this.props.match.path + '/contact-order'} 
	      	render={()=> (<ContactData ingredients={this.state.ingredients} totalPrice={this.state.totalPrice}/>)} />
	    </div>
		)
	}
}

export default Checkout;
