import React, { Component } from 'react';
import classes from './ContactData.css';
import Button from '../../../components/UI/Button/Button';
import axios from '../../../axios-order';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {
	state = {
		name: '',
		email: '',
		phone: '',
		address: {
			street: '',
			postalCode: ''
		},
		deliverMethod: '',
		loading: false
	}

	cancelCheckoutHandler = (event) => {
		event.preventDefault();
		this.setState({ loading: true });
		const order ={
			ingredients: this.props.ingredients,
			price: this.props.totalPrice,
			customer: {
				name: 'Ali Raza',
				address: 'Johar town lahore',
				email: 'ali@g.com'
			},
			deliverMethod: 'fastest'
		}

		axios.post('/orders.json', order).then(response => {
			this.setState({loading: false});
			this.props.history.push('/');
			console.log(response);
		})
	}

	inputChangeHandler = (event) => {
		console.log(event.target.value);
	}

	render () {
		let spinner = null;
		if(this.state.loading){
			spinner = <Spinner />
		}
		return (
			<div className={classes.ContactData}>
				{spinner}
				<h1> Please enter your contact detials</h1>
				<form>
					<Input inputtype="input" type="text" onChange={this.inputChangeHandler.bind(this, 'name')} 
								 value={this.state.name} name="name" placeholder="Your Name" />
					<Input inputtype="input" type="text" onChange={this.inputChangeHandler.bind(this, 'email')} 
								 value={this.state.email} name="email" placeholder="Your Email"/>
					<Input inputtype="input" type="text" onChange={this.inputChangeHandler.bind(this, 'phone')} 
								 value={this.state.phone} name="phone" placeholder="Your Phone Number"/>
					<Input inputtype="input" type="text" onChange={this.inputChangeHandler.bind(this, 'street')} 
								 value={this.state.address.street} name="street" placeholder="Your Street"/>
					<Input inputtype="input" type="text" onChange={this.inputChangeHandler.bind(this, 'postalCode')} 
					       value={this.state.address.postalCode} name="postalCode" placeholder="Your PostalCode"/>
					<Input inputtype="select" type="select" onChange={this.inputChangeHandler.bind(this, 'deliveryMethod')} 
								 value={this.state.address.deliveryMethod} name="deliveryMethod" placeholder="Your Delivery Method"
								 options={[{value: 'fastest', name: 'Fastest'}, {value: 'slow', name: 'Slowest'}]}/>
					<Button text="ORDER" click={this.cancelCheckoutHandler.bind(this)} btnType="Danger"/>
				</form>
			</div>
		)
	}
}

export default ContactData;
