import React, { Component } from 'react';
import classes from './ContactData.css';
import Button from '../../../components/UI/Button/Button';
import axios from '../../../axios-order';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {
	state = {
		name: '',
		email: '',
		phone: '',
		address: {
			street: '',
			postalCode: ''
		},
		loading: false
	}

	cancelCheckoutHandler = (event) => {
		event.preventDefault();
		this.setState({ loading: true });
		const order ={
			ingredients: this.props.ingredients,
			price: this.props.totalPrice,
			customer: {
				name: 'Ali Raza',
				address: 'Johar town lahore',
				email: 'ali@g.com'
			},
			deliverMethod: 'fastest'
		}

		axios.post('/orders.json', order).then(response => {
			this.setState({loading: false});
			this.props.history.push('/');
			console.log(response);
		})
	}

	render () {
		let spinner = null;
		if(this.state.loading){
			spinner = <Spinner />
		}
		return (
			<div className={classes.ContactData}>
				{spinner}
				<h1> Please enter your contact detials</h1>
				<form>
					<Input type="text" name="name" placeholder="Your Name"/>
					<Input type="text" name="email" placeholder="Your Email"/>
					<Input type="text" name="Phone Number" placeholder="Your Phone Number"/>
					<Input type="text" name="street" placeholder="Your Street"/>
					<Input ype="text" name="postalCode" placeholder="Your PostalCode"/>
					<Button text="ORDER" click={this.cancelCheckoutHandler.bind(this)} btnType="Danger"/>
				</form>
			</div>
		)
	}
}

export default ContactData;
