import React from 'react';
import classes from './OrderSummary.css';
import Button from '../../UI/Button/Button';
import btnclasses from '../../UI/Button/Button.css';

const orderSummary = (props) => {
	let orderList = Object.keys(props.ingredients).map( igName => {
		return <li key={igName}>{igName.charAt(0).toUpperCase() + igName.slice(1)} : {props.ingredients[igName]}</li>	
	})

	return (
		<div>
			<div>
				<span className={classes.OrderHeading}> Your Order </span>
				<span onClick={props.orderingHandler.bind(this)} className={classes.CancelButton}> X </span>
			</div>
			<p> A delicious burger with following ingredients: </p>
			<ul>
				{ orderList }
			</ul>
			<div className={btnclasses.Button}>
				<Button text="CANCEL" click={props.orderingHandler.bind(this)}  class={btnclasses.Danger}/>
				<Button text="CONTINUE" click={props.orderingHandler.bind(this)} class={btnclasses.Success}/>
			</div>
		</div>
	)
}

export default orderSummary;
