import React, { Component } from 'react';
import Aux from '../../hoc/Aux';
import { connect } from 'react-redux';
import Burger from '../../components/Burger/Burger';
import BurgerControls from '../../components/Burger/BurgerControls/BurgerControls';
import Modal from '../../components/UI/Modal/Modal';
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary';
import Spinner from '../../components/UI/Spinner/Spinner';
import axios from '../../axios-order';
import WithErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';
import * as actionTypes from '../../Store/actions';

const INGREDIENTS_PRICES = {
	salad: 0.5,
	cheese: 0.5,
	bacon: 0.2,
	meat: 2
}

class BurgerBuilder extends Component {
	state = {
		ingredients: {},
		totalPrice: 4,
		purchaseable: false,
		ordering: false,
		loading: false
	}

	componentDidMount () {
		axios.get('https://burger-labz.firebaseio.com/ingredients.json').then(response =>{
			this.props.setIngredientsHandler(response.data);
		});
	}

	ingredientsIncementHandler = (ingredientName) => {
		let ingredientCount = this.state.ingredients[ingredientName]
		let ingredients = {...this.state.ingredients};
		ingredients[ingredientName] = ingredientCount + 1;
		let burgerPrice = INGREDIENTS_PRICES[ingredientName] + this.state.totalPrice;
		this.setState({ ingredients: ingredients, totalPrice: burgerPrice, purchaseable: true });
	}

	updateOrderNow = (ingredients) => {
		let updatedIngredients = {...ingredients};
		let ingredientsSum = 0;

		for (let key in updatedIngredients) {
			ingredientsSum += updatedIngredients[key]
		}
		this.setState({purchaseable: ingredientsSum > 0})
	}

	ingredientsDecrementHandler = (ingredientName) => {
		let ingredientCount = this.state.ingredients[ingredientName]
		let ingredients = {...this.state.ingredients};
		ingredients[ingredientName] = ingredientCount - 1;
		let burgerPrice = this.state.totalPrice - INGREDIENTS_PRICES[ingredientName];

		if (ingredientCount > 0) {
			this.setState({ ingredients: ingredients, totalPrice: burgerPrice });
		}
		this.updateOrderNow(ingredients)
	}

	orderNowHandler = () => {
		this.setState({ ordering: true })
	}

	orderCancelHandler = () => {
		this.setState({ ordering: false })
	}

	orderContinueHandler = () => {
		// alert('Your order is recived and we begin preparing !');

		const queryparams = [];
		for (let i in this.state.ingredients) {
			queryparams.push(encodeURIComponent(i) + '=' + encodeURIComponent(this.state.ingredients[i]))
		}
		queryparams.push(encodeURIComponent('totalPrice') + '=' + this.state.totalPrice)
		this.props.history.push({
			pathname: '/burger-checkout',
			search: '?' + queryparams.join('&')
		});
	}

	render () {
		const disabledInfo = {
			...this.state.ingredients
		}

		for (let key in disabledInfo) {
			disabledInfo[key] = disabledInfo[key] <= 0
		}
		let orderSummary = <OrderSummary 
													ingredients={this.state.ingredients} 
													orderCancelHandler={this.orderCancelHandler}
													orderContinueHandler={this.orderContinueHandler}
													totalPrice={this.state.totalPrice}
												/>
		if(this.state.loading) {
			orderSummary = <Spinner />
		}

		return (
			<Aux>
				<Modal show={this.state.ordering} orderCancelHandler={this.orderCancelHandler.bind(this)}>
					{ orderSummary }
				</Modal> 
				<Burger ingredients={this.props.ingredients} />
				<BurgerControls 
					price={this.state.totalPrice}
					ingredients={this.props.ingredients} 
					incrementHandler={this.ingredientsIncementHandler}
					decrementHandler={this.ingredientsDecrementHandler}
					disabledInfo={disabledInfo}
					purchaseable={this.state.purchaseable}
					orderNowHandler={this.orderNowHandler}/>
			</Aux>
		)
	}
} 

const mapStateToProps = state => {
	return {
		ingredients: state.ing.ingredients
	}
}

const mapDispatchToProps = dispatch => {
	return {
		setIngredientsHandler: (data) => dispatch({type: actionTypes.SET_INGREDIENTS, ingredients: data})
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(WithErrorHandler(BurgerBuilder, axios));
