import * as actionTypes from '../actions/actionTypes';
import { uilityContent } from '../utility';

const intialState = {
	loading: false
}

const orderReducer = (state = intialState, action) => {
	switch(action.type) {
		case actionTypes.TRY_SUBMIT:
			return {
				loading: true
			}
		default:
			return state
	}
}

export default orderReducer;
