import React, { Component } from 'react';
import classes from './ContactData.css';
import Button from '../../../components/UI/Button/Button';

class ContactData extends Component {
	state = {
		name: '',
		email: '',
		phone: '',
		address: {
			street: '',
			postalCode: ''
		}
	}

	cancelCheckoutHandler = () => {
		this.props.history.push('/burger');
	}

	render () {
		return (
			<div className={classes.ContactData}>
				<h1> Please enter your contact detials</h1>
				<form>
					<input className={classes.Input} type="text" name="name" placeholder="Your Name"/>
					<input className={classes.Input} type="text" name="email" placeholder="Your Email"/>
					<input className={classes.Input} type="text" name="Phone Number" placeholder="Your Phone Number"/>
					<input className={classes.Input} type="text" name="street" placeholder="Your Street"/>
					<input className={classes.Input} type="text" name="postalCode" placeholder="Your PostalCode"/>
					<Button text="Order" click={this.cancelCheckoutHandler.bind(this)} btnType="Danger"/>
				</form>
			</div>
		)
	}
}

export default ContactData;
